pragma solidity ^0.8.9;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./VotingToken.sol";
import "./NFT.sol";

// Contrato de la DAO
contract DAO is Ownable{
    enum statusProposal { PENDING, CLOSED }

    Proposal[] proposals;
    
    struct Proposal {
        uint256 id;
        address creator;
        uint256 startTime;
        uint256 endTime;
        string description;
        uint256 optionA;
        uint256 optionB;
        statusProposal status;
    }   

    mapping(address => mapping(uint => bool)) public votes;    

    VotingToken public votingToken; // Contrato del token de votación

    NFT public nftContract; // Contrato del NFT
        
    // Eventos
    event ProposalCreated(uint256 proposalId, address creator, string description, uint256 endTime);
    event Voted(uint256 proposalId, address voter, bool inFavor, uint256 tokenId);
     // Event for a proposal execution
    event ProposalExecuted(uint256 indexed proposalId, address indexed proposer);

    
    // Constructor
    constructor(address _votingTokenAddress, address _nftContractAddress) {
        votingToken = VotingToken(_votingTokenAddress);
        nftContract = NFT(_nftContractAddress);
    }
    
    // Función para crear una nueva propuesta
    function createProposal(string calldata _description) external onlyOwner{
        require(votingToken.balanceOf(msg.sender) > 0, "Insufficient tokens to create proposal");
        // require(!activeProposals[msg.sender], "You already have an active proposal");
        // EnumerableSet.AddressSet storage enumerableVoters;
        // address[] memory voters;

        // Proposal storage newProposal = Proposal[8];

        // newProposal.id = proposals.length + 1;
        // newProposal.description = msg.sender;
        // newProposal.description = _description;
        // newProposal.startTime = block.timestamp;
        // newProposal.endTime = block.timestamp + 5 minutes;
        // newProposal.optionA = 0;
        // newProposal.optionB = 0;
        // newProposal.status = statusProposal.PENDING;

    
        Proposal memory newProposal = Proposal({
            id: proposals.length,
            creator: msg.sender,
            description: _description,
            startTime: block.timestamp,
            endTime: block.timestamp + 5 minutes,
            optionA: 0,
            optionB: 0,
            status: statusProposal.PENDING
        });
    
        proposals.push(newProposal);
        // activeProposals[msg.sender] = true;
        emit ProposalCreated(newProposal.id, msg.sender, _description, newProposal.endTime);
        // proposalCount++;
    }
    
    // Función para votar en una propuesta
    function vote(uint256 _proposalId, bool _inFavor) external {
        // require(_proposalId < proposalCount, "Invalid proposal ID");
        Proposal storage proposal = proposals[_proposalId];
        // require(!proposal.hasVoted[msg.sender], "You have already voted");
        // require(block.timestamp < proposal.endTime, "Voting has ended for this proposal");
        require(proposal.status != statusProposal.CLOSED, "Proposal has already been executed");
        require(block.timestamp >= proposal.startTime && block.timestamp <= proposal.endTime, "Invalid voting period");        
        require(votes[msg.sender][_proposalId] == false, "You have already voted for this proposal");
        // require(!proposal.voters.contains(msg.sender), "You have already voted on this proposal");
        // require(!proposal.voters[msg.sender] = true, "You have already voted on this proposal");
        // proposal.hasVoted[msg.sender] = true;
        // proposal.voters.push(msg.sender);
        
        // uint256 voterPower = votingPower[msg.sender];
        // require(voterPower > 0, "You have no voting power");
        require(votingToken.balanceOf(msg.sender) > 0, "Insufficient tokens to vote");
        
        // if (_inFavor) {
        //     proposal.optionA += 1;
        // } else {
        //     proposal.optionB += 1;
        // }
        
        if (_inFavor) {
            proposal.optionA++;
        } else {
            proposal.optionB++;
        }

        votes[msg.sender][_proposalId] = true;

        // proposal.voters.add(msg.sender);

        // proposal.voters[msg.sender] = true;

        // Transfiere los tokens de votación desde el votante hacia la DAO
        // votingToken.transferFrom(msg.sender, address(this), voterPower);
        
        // Genera un nuevo NFT para el votante y agrega los datos de la propuesta
        uint256 newTokenId = nftContract.mint(msg.sender, _proposalId, proposal.description);
        
        emit Voted(_proposalId, msg.sender, _inFavor, newTokenId);
    }
    
    // Función para obtener el estado de una propuesta
    function getProposalStatus(uint256 _proposalId) external view returns (address, string memory, uint256, uint256, uint256, uint256) {
        // require(_proposalId < proposalCount, "Invalid proposal ID");
        Proposal storage proposal = proposals[_proposalId];
        return (proposal.creator, proposal.description, proposal.startTime, proposal.endTime, proposal.optionA, proposal.optionB);
    }

    // Function to execute a proposal
    function executeProposal(uint256 _proposalId) external {
        Proposal storage proposal = proposals[_proposalId];
        // require(!proposal.executed, "Proposal has already been executed");
        require(proposal.status != statusProposal.CLOSED, "Proposal has already been executed");
        require(block.timestamp > proposal.endTime, "Voting period is still ongoing");
        // require(proposal.optionA > proposal.optionB, "Proposal has not reached majority support");
    
        proposal.status = statusProposal.CLOSED;
        // activeProposals[proposal.proposer] = false;
        emit ProposalExecuted(proposal.id, proposal.creator);
    }

    function getProposals() external view returns ( Proposal[] memory) {     
       return proposals;
    }

