pragma solidity ^0.8.9;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";

contract NFT is ERC721 {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    
    struct NFTData {
        uint256 proposalId;
        string description;
    }
    
    mapping(uint256 => NFTData) private nftData;
    
    constructor() ERC721("Voting NFT", "VNFT") {}
    
    // Función para crear un nuevo NFT y asignarlo a una dirección
    function mint(address _to, uint256 _proposalId, string memory _description) external returns (uint256) {
        _tokenIds.increment();
        uint256 newTokenId = _tokenIds.current();
        _mint(_to, newTokenId);
        
        nftData[newTokenId] = NFTData(_proposalId, _description);
        
        return newTokenId;
    }
    
    // Función para obtener los datos de un NFT
    function getNFTData(uint256 _tokenId) external view returns (uint256, string memory) {
        require(_exists(_tokenId), "Invalid NFT token ID");
        NFTData storage data = nftData[_tokenId];
        return (data.proposalId, data.description);
    }
}","keccak256":"0x306ef51762e438b579c5af51df313021328834c25d2dfed42fef94855ecc31e5","license":"MIT"},"contracts/VotingToken.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract VotingToken is ERC20, Ownable {
    mapping(address => uint256) public votingPower;
    
    constructor() ERC20("Voting Token", "VOTE") {
        _mint(msg.sender, 1000000 * 10 ** decimals());
    }
    
    // Función para asignar el poder de voto a una dirección
    function assignVotingPower(address _voter, uint256 _votingPower) external {
        require(msg.sender == owner(), "Only owner can assign voting power");
        votingPower[_voter] = _votingPower;
    }
}","keccak256":"0x74689076a3bf47726a0fab617db4377e517c77142f5c61c7afd46904a113a2a2","license":"MIT"}},"version":1}
